export const FrameworkLogo = ({ framework, ...rest }) => {
  switch (framework) {
    case 'react':
      return <ReactLogo {...rest} />;
    case 'angular':
      return <AngularLogo {...rest} />;
    case 'vue':
      return <VueLogo {...rest} />;
    case 'flutter':
      return <FlutterLogo {...rest} />;
    default:
      return <ReactLogo {...rest} />;
  }
};

